INSERT INTO `projects` VALUES ('71', '138', '热备份路由协议', '2016-11-01', '2016-12-01', null, ' 网络线路搭建，命令总结', ' 1.搭建由两台核心交换机做热备份的公司网络。 2.在网络中实现VLAN的负载均衡。 3.分别用HSRP和VRRP实现。');
