INSERT INTO `projects` VALUES ('175', '450', '员工贡献积分管理系统', '2018-07-01', '2018-08-01', null, null, ' 员工贡献积分管理系统用于员工报名悬赏任务挣积分、用积分兑换假期和物品、查看自己的积分明细台账；管理员可通过导入员工每周的考勤情况或员工的通报奖惩情况来更新员工的贡献积分数，也可通过系统发布悬赏任务，任务完成后系统自动分配积分到相应员工的积分明细台账中。');
