INSERT INTO `projects` VALUES ('228', '588', '禾中一号产业基金', '2017-03-01', '2017-06-01', null, ' 作为内勤，跟公司业务员沟通合同细节，实时跟进业务合同的签订以及后续事宜，并实现与总部相关负责人的流程对接，并做好相关项目的台账明细，整理并进行数据分析', ' 公司拓展业务范畴，与总公司共同负责禾中产业一号基金的发售工作，一号基金分期发售，公司负责一期到四期的发售');
