INSERT INTO `projects` VALUES ('289', '774', '世贸钢材供应商管理系统', '2017-05-01', '2017-10-01', null, null, ' 项目简介项目主要分为六个模块 1. 供应商的注册登录功能； 2.商品管理主要是在线提报商品和打印条码； 3.采购系统主要是供应商对采购信息进行确认和发货； 4商品进销存主要是提供供应商查询商品销售状态及库存信息； 5.售后服务主要是对维修和配件进行订单进行确认和操作； 6.结算管理主要是查询自己的结算信息及一些奖惩信息。');
