INSERT INTO `projects` VALUES ('832', '2163', '公司宣传片的拍摄', '2017-08-01', '2017-11-01', null, ' 1.宣传片拍摄的前期沟通（企业和产品的特点，提案大纲，资料准备，人员安排等） 2.拍摄中全程协助，同时留取员工参加拍摄时的照片作为企业文化的宣传。 3.宣传片后期的细节把控。（镜头的画面清晰度、颜色配比、声音的大小等） 4.完成', ' 根据公司的发展需要，通过宣传片的拍摄提高公司在行业的知名度。');
