INSERT INTO `projects` VALUES ('1059', '2758', '联通挂机短信平台', '2013-03-01', '2013-06-01', null, ' 需求分析、平台架构设计、原型设计。', ' 挂机短信产品是指移动终端用户拨打河北联通固定电话，在通话结束后，将收到由被叫用户发送的预先设置好内容的短信，短信的发送号码为固定电话号码，短信内容可以是介绍被叫用户相关企业信息（如产品介绍、促销活动、客户关怀信息等），可以很好地满足企业客户的信息发布需求。 此外，挂机短信系统还可以自动、准确收集拨打开通来电短信产品的固话用户的电话号码信息，节省用户电话录入的时间，提供简明客户资料管理功能。');
