INSERT INTO `projects` VALUES ('1446', '3835', '《石家庄首届“海帆杯”亲子游泳锦标赛》大型活动', '2017-07-01', '2017-09-01', null, ' 活动的策划，启动，推进的总负责人', ' 筹划和推进《石家庄首届“海帆杯”亲子游泳锦标赛》大型地推活动，比赛历时2个月，参赛人次400多组家庭，吸收非会员报名200多组，转化9个，达到销售转化、活跃老会员、提升品牌知名度和影响力的目标，整体效果超出预期。');
